mapper.select:
SELECT memberIdx, userId, userName, userPwd, tel, zip, addr1, addr2, birth, email, introduce, profile_imageFilename
FROM member
WHERE userId = #{userId}

mapper.update:
UPDATE member2 SET email=#{email, jdbcType=VARCHAR}, 
             tel=#{tel, jdbcType=VARCHAR}, birth=#{birth, jdbcType=VARCHAR},
             zip=#{zip, jdbcType=VARCHAR},
             addr1=#{addr1, jdbcType=VARCHAR}, addr2=#{addr2, jdbcType=VARCHAR}
       WHERE userId=#{userId}
