mapper.select:
SELECT sellerId, sellerName, pwd, tel, email, created_date, modify_date, allow, profile_imageFilename, enabled
FROM seller
WHERE sellerId = #{sellerId}

SELECT sellerId, sellerName, pwd, tel, email, created_date, modify_date, allow, profile_imageFilename, enabled
FROM seller
WHERE sellerId = #{sellerId}

mapper.update:
UPDATE seller SET pwd=#{pwd}, email=#{email, jdbcType=VARCHAR}, modify_date=#{modify_date},
             tel=#{tel, jdbcType=VARCHAR}, birth=#{birth, jdbcType=VARCHAR},
       WHERE sellerId=#{sellerId}
